import React from 'react';
import './App.css';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import {connect} from 'react-redux';
import {SUBMIT_FORM} from './actionCreators/actionTypes';
import {store} from './store.js'

const mapStateToProps = (state) => {
	return {
      email : state.email,
      mobile : state.mobile,
      first_name : state.first_name,
      last_name : state.last_name,
		// careType : state.overallReport.careType,
		// department : state.overallReport.department,
		// unit: state.overallReport.unit,
		// selected : {...state.overallReport.selected}, 
		// descriptiveList : state.overallReport.JSON.descriptive,
		// bacteriaList : state.overallReport.JSON.bacteria,
		// token: state.auth.token,
		// startDate: state.overallReport.startDate,
		// endDate: state.overallReport.endDate,
		// unit: state.overallReport.unit,
		// showMDRO : state.overallReport.showMDRO,
	}
}

const mapDispatchToProps = (dispatch) => ({
	// onListUpdate : (value) => dispatch ({type:UPDATE_JSON_DATA, key: 'descriptive', value }),
	// onSelectionChange : (key,value) => dispatch({'type':UPDATE_SELECTION_DATA, 'key' : key, value}),
	// onShowMDRO : (value) => dispatch ({type: UPDATE_FIELD_HEADER, key:'showMDRO',value}),
	// onInitialiseAntibiotic : ()=> dispatch({type : UPDATE_SELECTION_DATA,key : 'antibiotic',value : false}),
	// onInitialiseBacteria : () => {
	// 	dispatch({type:UPDATE_SELECTION_DATA, key: 'bacteria' , value : false});
  // 	dispatch({type:UPDATE_SELECTION_DATA, key: 'mdro' , value :false});
  onFillupFields: (value) => dispatch ({type: SUBMIT_FORM, key: 'submitForm', value}),
})





function submit(opts){
  fetch('https://rdv.org/register', {
    method: 'post',
    body: JSON.stringify(opts)
  }).then(function(response) {
    return response.json();
  });
}

class App extends React.Component {
  constructor(props){
    super(props);
    this.state={
      visiblity : true
    }
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange = event => {
    console.log(event.target.value)
    this.setState({
        email : event.target.value,
      })
  }

  handleChange1 = event => {
    console.log(event.target.value)
    this.setState({
        mobile : event.target.value,
      })
  }

  render() {
    return (
      <div>
        <Form>
          <FormGroup>
            <Label for="exampleEmail">Email</Label>
            <Input type="email" name="email" id="exampleEmail" placeholder="with a placeholder"  onChange = {event => this.handleChange(event)} />
          </FormGroup>
          <FormGroup>
            <Label for="Mobile">Mobile</Label>
            <Input type="mobile" name="mobile" id="mobile" placeholder="with a placeholder"  onChange = {event => this.handleChange1(event)} />
          </FormGroup>
          <Button onClick={submit(this.state)}>Submit</Button>
        </Form>
      </div>
    )  
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(App);
